import React, { useState } from 'react';
import {CardNumberElement, CardExpiryElement, CardCvcElement, useStripe,} from '@stripe/react-stripe-js';

export const Form = () => {
  const stripe = useStripe();
  const [cardNumber, setCardNumber] = useState("");
  const [cardExpiry, setCardExpiry] = useState("");
  const [cardCvc, setCardCvc] = useState("");
  const [userFirst, setUserFirst] = useState("");
  const [userLast, setUserLast] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");



  const handleSubmit = async (event) => {
    // Block native form submission.
    event.preventDefault();
    console.log(cardNumber, cardExpiry, cardCvc, userFirst, userLast, city, state, zip)

    if (!stripe) {
      // Stripe.js has not loaded yet. Make sure to disable
      // form submission until Stripe.js has loaded.
      return;
    }

    // Get a reference to a mounted CardElement. Elements knows how
    // to find your CardElement because there can only ever be one of
    // each type of element.
    // Use your card Element with other Stripe.js APIs



  };

  return (
    <form onSubmit={handleSubmit}>
      <CardNumberElement onChange={(newCardNumber) => {
        console.log({value: newCardNumber});

        setCardNumber({value: newCardNumber});
      }}
      options={{value: cardNumber}}/>
      <CardExpiryElement onChange={(newCardExpiry) => setCardExpiry(newCardExpiry)} options={{value: cardExpiry}}/>
      <CardCvcElement onChange={(newCardCvc) => setCardCvc(newCardCvc)} options={{value: cardCvc}}/>
      <br></br>
      <input type="text" value={userFirst} onChange={(event) => setUserFirst(event.target.value)} name="First name" />
      <br></br>
      <input type="text" value={userLast} onChange={(event) => setUserLast(event.target.value)} />
      <br></br>
      <input type="text" value={city} onChange={(event) => setCity(event.target.value)} />
      <br></br>
      <input type="text" value={state} onChange={(event) => setState(event.target.value)} />
      <br></br>
      <input type="text" value={zip} onChange={(event) => setZip(event.target.value)} />
      <br></br>
      <button type="submit" disabled={!stripe}>
        Pay
      </button>
    </form>
  );
};
